          PER_64 = 3'b010,
          PER_8K = 3'b100
        }
    } prng_reseed_rate ;
    logic      sideload;
    {
        enum logic [AES_KEYLEN_WIDTH-1:0] {
          AES_128 = 3'b001,
          AES_192 = 3'b010,
          AES_256 = 3'b100
        }
    } key_len ;
    {
        enum logic [AES_MODE_WIDTH-1:0] {
          AES_ECB  = 6'b00_0001,
          AES_CBC  = 6'b00_0010,
          AES_CFB  = 6'b00_0100,
          AES_OFB  = 6'b00_1000,
          AES_CTR  = 6'b01_0000,
          AES_NONE = 6'b10_0000
        }
    } mode ;
    {
        enum logic [AES_OP_WIDTH-1:0] {
          AES_ENC = 2'b01,
          AES_DEC = 2'b10
        }
    } operation ;

}

